import React, { Component, Fragment } from 'react';
import '../App.css';
// import AllCountries from "./AllCountries";

class Search extends Component {
  // constructor(props) {
  //   super(props); // need this keyword for state !!!!!!!!!!
    state = {
      value: [],
      names:[],
      confirmed: null,
      recovered:null,
      deaths:null
    };
    // this.handleChange = this.handleChange.bind(this);
    // this.handleSubmit = this.handleSubmit.bind(this);
  // }



  // ComponentDidMount
  componentDidMount = () =>{
    fetch(`https://covid19.mathdro.id/api/countries/`)
    .then(response => response.json())
    .then(data => {
      console.log("TEST_ISO3", data.countries);
      let iso3Array = [];

      let iso3 = Object.keys(data.iso3);
      iso3.map(key => {
        iso3Array.push(key);
      });
      let namesOfCountries = Object.keys(data.countries);
      let optionsArray = [];
      namesOfCountries.map(key => {
        optionsArray.push(key);
      });
      this.setState({names: optionsArray, value:iso3Array});
    });
  }


  // HandleChange
  handleChange = (event) => {
    let val = event.target.value;

    console.log("val" , val);
    console.log("State from HandleChange", this.state.value);
    // this.state.value
    fetch( `https://covid19.mathdro.id/api/countries/AX`)
    .then(response => response.json())
    .then(data => {
      let confirmed = data.confirmed.value;
      let recovered = data.recovered.value;
      let deaths = data.deaths.value;
      this.setState({confirmed, recovered, deaths})
    });
      // console.log("state inside change",this.state);
  };

  // HandleSubmit
  handleSubmit(event) {
    alert('Your favorite flavor is: ' + this.state.value);
    event.preventDefault();
    console.log("Submitted");
  }


  // Render function 
  render() {
      console.log("State Inside render", this.state);
      const { names, value, confirmed, recovered, deaths } = this.state;
      // console.log("From Render", confirmed);

      const renderName = names.map((el,index) => {
        // console.log("From renderName", el);
        return(
          <Fragment key={index}>
            <option value="asd">{el}</option>
          </Fragment>
        )
      });
return (
  <form onSubmit={this.handleSubmit}>
    <label>
      <select value="" onChange={this.handleChange}>
        {renderName}

      </select>
    </label>
  </form>
);
  }
}

export default Search;
