import React, { Component } from 'react'
import styled from 'styled-components'

class Jo extends Component {
  state = {
    global: "mansour"
  };

  componentDidMount(){
    fetch("https://covid19.mathdro.id/api/countries/Jordan")
    .then(response => response.json())
    .then(data => {      
      let globalConfirmed = data.confirmed.value;
      let globalRecovered = data.recovered.value;
      let globalDeaths = data.deaths.value;
      let update = data.lastUpdate;
      let global = [globalConfirmed, globalRecovered, globalDeaths, update];
      this.setState({ global:global })
    });
  }

  render() {
    let som = this.state
    let update = som.global[3]
    let toUpdate = (update && (<div>Updated: {new Date(update).toLocaleString()}</div>))
    return (
      <div className="entireData-items">
        <h1>Jordan</h1>
        <div className="global-item">
          <P style={{ "color": "orange"}}> {som.global[0]} </P>
          <Label  style={{"color": "orange"}}>confirmed</Label>
        </div>
        <div className="global-item">
          <P style={{"color": "green"}}> {som.global[1]}</P>
          <Label style={{"color": "green"}}>recovered</Label>
        </div>
        <div className="global-item">
          <P style={{"color": "red"}}> {som.global[2]}</P>
          <Label style={{"color": "red"}}>deaths</Label>
        </div>
        <br></br><br></br>
        <DivUpdate style={{"fontSize":"10px", "color":"white"}}>toUpdate</DivUpdate>
      </div>
    )
  }
}

export default Jo;


const P = styled.p`
  font-weight:bold;
  font-size:30px;
`
const Label = styled.label`
  font-size:16px;
  font-weight: bold;
`
const DivUpdate = styled.div`
  font-size: 10px;
  color: white;
`
