import React, { Component, Fragment } from 'react';
import '../App.css';
import AllCountries from "./AllCountries";

class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {value: [], names:[]};
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidMount(){
    fetch(`https://covid19.mathdro.id/api/countries/`)
    .then(response => response.json())
    .then(data => {
      let iso3 = Object.keys(data.iso3)
      // console.log(iso3);
      let iso3Array = []
      iso3.map(key => {
        iso3Array.push(key)
        // console.log("iso3Array", iso3Array);        
      })
      // 
      let namesOfCountries = Object.keys(data.countries)
      let optionsArray = []
      namesOfCountries.map(key => {
        optionsArray.push(key)
        // console.log("optionsArray", optionsArray);        
      })
      this.setState({names:optionsArray, value:iso3Array})
    });
  }



  handleChange(event) {
    this.setState({value: event.target.value});
    // return <AllCountries/>
    console.log("changed" , event.target.value);
    return (
      <div>Hello</div>
    )
  }

  handleSubmit(event) {
    alert('Your favorite flavor is: ' + this.state.value);
    event.preventDefault();
    console.log("Submitted");

  }


  render() {
    const { names, value } = this.state;
    // console.log("names", names);

    const renderName = names.map((el,index) => {
      return(
        <Fragment key={index}>
          <option value={el}>{el}</option>
        </Fragment>
      )
    })
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          <select value="" onChange={this.handleChange}>
            {renderName}
            <div className="entireData-items">
        <h2>الأردن</h2>
