import React, { Component } from 'react';
import '../App.css';

const options = [
  { value: 'chocolate', label: 'Chocolate' },
  { value: 'strawberry', label: 'Strawberry' },
  { value: 'vanilla', label: 'Vanilla' },
];

class Search extends Component {

  state = {
    data: null,
    selectedOption: null
  };



  componentDidMount(){
    fetch(`https://covid19.mathdro.id/api/countries/`)
    .then(response => response.json())
    .then(data => {
      let g = Object.keys(data.countries)
      let optionsArray = []
      g.map(key => {
        // console.log("KEY", key);
        optionsArray.push(key)
        console.log("optionsArray", optionsArray);

      })
      this.setState({selectedOption: optionsArray})
    });
  }

  handleChange (value) {
    this.setState({selected: value})
  }


  render() {
    const { selectedOption } = this.state;
    return (
      <div className="app">
        <div className="container">
          <h1>Search</h1>
          <div style={{maxWidth: '300px', margin: '50px auto'}}>
        <select
          className='my-custom-css'
          placeholder={'Social Networking'}
          value={this.state.selected}
          source={options}
          onChange={this.handleChange.bind(this)}
          options={this.state.selectedOption}
        />
      </div>

          {/* <div className="dataContainer" >{this.state.data}</div> */}
        </div>
      </div>

    );
  }
}

export default Search;
