    state = {
      value: [],
      names:[],
      confirmed: null,
      recovered:null,
      deaths:null
    };
    // this.handleChange = this.handleChange.bind(this);
    // this.handleSubmit = this.handleSubmit.bind(this);
  // }

  componentDidMount = () =>{
    fetch(`https://covid19.mathdro.id/api/countries/`)
    .then(response => response.json())
    .then(data => {
      let iso3 = Object.keys(data.iso3)
      // console.log(iso3);
      let iso3Array = []
      iso3.map(key => {
        iso3Array.push(key)
        // console.log("iso3Array", iso3Array);        
      })
      // 
      let namesOfCountries = Object.keys(data.countries)
      let optionsArray = []
      namesOfCountries.map(key => {
        optionsArray.push(key)
        // console.log("optionsArray", optionsArray);        
      })
      this.setState({names:optionsArray, value:iso3Array})
    });
  }



  handleChange = (event) => {
    let val = event.target.value;
    console.log("changed" , val);
    fetch( `https://covid19.mathdro.id/api/countries/af`)
    .then(response => response.json())
    .then(data => {
      console.log("handleChange", data);
      let confirmed = data.confirmed.value;
      let recovered = data.recovered.value;
      let deaths = data.deaths.value;

      this.setState({confirmed, recovered, deaths})
    });
      console.log("state inside change",this.state);

  }

  handleSubmit(event) {
    alert('Your favorite flavor is: ' + this.state.value);
    event.preventDefault();
    console.log("Submitted");
  }


  render() {
            // console.log("inside render", this.state);
      const { names, value, confirmed, recovered, deaths } = this.state;
      console.log(confirmed);

      const renderName = names.map((el,index) => {
        return(
          <Fragment key={index}>
            <option value={el}>{el}</option>
          </Fragment>
        )
      })

      // console.log("names", names);
      // if(selected === null) {
      //   return(
      //     <div>
      //       <select value="" onChange={this.handleChange}>
      //         {renderName}  
      //       </select>
      //     </div>
      //   )
      // }

return (
  <form onSubmit={this.handleSubmit}>
    <label>
      <select value="" onChange={this.handleChange}>
        {renderName}

      </select>
    </label>
  </form>
);
  }
}

export default Search;
