import React, { Component } from 'react'
import "../App.css"
export default class InstructionTicker extends Component {

  state={
    data:""
  }

  componentDidMount = () => {
    fetch("http://newsapi.org/v2/top-headlines?country=ae&category=health&apiKey=f869bbb8172a4c53b25182b5fed0ca61")
    .then(response => response.json())
    .then(data => {
      console.log(data.articles);
      let news = data.articles.forEach((article) => {
        if(article.title.includes("كورونا")) {
          return `<a href=${article.url}>article.title</a>`
          console.log(`<a href=${article.url}>article.title</a>`);
        } 
        this.setState({data:news})
      })

    })
  }

  render() {
    let {news} = this.state;
    return (
      <div className="tcontainer">
        <div className="ticker-wrap">
        <div className="ticker-move">

          <div className="ticker-item">{news}</div>

        </div>
      </div>
      </div>
    )
  }
}
