import React, { Component } from 'react'

class Jo extends Component {
  state = {
    global: "mansour"
  };

  componentDidMount(){
    fetch("https://covid19.mathdro.id/api/countries/Jordan")
    .then(response => response.json())
    .then(data => {      
      let globalConfirmed = data.confirmed.value;
      let globalRecovered = data.recovered.value;
      let globalDeaths = data.deaths.value;
      let update = data.lastUpdate;

      let global = [globalConfirmed, globalRecovered, globalDeaths, update];
      this.setState({ global:global })
    });
  }

  render() {
    let som = this.state
    let update = som.global[3]
    return (
      <div className="entireData-items">
        <h2>Jordan</h2>
        <div className="global-item">
          <p style={{"color": "orange", "fontWeight":"bold", "fontSize":"30px"}}> {som.global[0]} </p>
          <label  style={{"color": "orange"}}>confirmed</label>
        </div>
        <div className="global-item">
          <p style={{"color": "green", "fontWeight":"bold", "fontSize":"30px"}}> {som.global[1]}</p>
          <label style={{"color": "green"}}>recovered</label>
        </div>
        <div className="global-item">
          <p style={{"color": "red", "fontWeight":"bold", "fontSize":"30px"}}> {som.global[2]}</p>
          <label style={{"color": "red"}}>deaths</label>
        </div>

        <br></br>
        <br></br>
        <div style={{"fontSize":"10px", "color":"white"}}>{ update && (<div>Updated: {new Date(update).toLocaleString()}</div>)}</div>

      </div>
    )
  }
}

export default Jo;
