import styled from 'styled-components'


// P
export const Confirmed = styled.p`
  color: orange;
  font-weight: bold;
  font-size: 30px;
`
export const Recovered = styled.p`
  color: green;
  font-weight: bold;
  font-size: 30px;
`
export const Deaths = styled.p`
  color: red;
  font-weight: bold;
  font-size: 30px;
`

// Labels
export const ConfirmedLabel = styled.label`
  color: orange;
`
export const RecoveredLabel = styled.label`
  color: green;
`
export const DeathsLabel = styled.label`
  color: red;
`
export const Label = styled.label`
  font-size:16px;
  font-weight: bold;
`
// Div
export const DivUpdate = styled.div`
  font-size: 10px;
  color: red;
`
