import React, { Component, Fragment } from 'react';
import '../App.css';
import AllCountries from "./AllCountries";

class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {value: [], names:[], selected: null};
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidMount(){
    fetch(`https://covid19.mathdro.id/api/countries/`)
    .then(response => response.json())
    .then(data => {
      let iso3 = Object.keys(data.iso3)
      // console.log(iso3);
      let iso3Array = []
      iso3.map(key => {
        iso3Array.push(key)
        // console.log("iso3Array", iso3Array);        
      })
      // 
      let namesOfCountries = Object.keys(data.countries)
      let optionsArray = []
      namesOfCountries.map(key => {
        optionsArray.push(key)
        // console.log("optionsArray", optionsArray);        
      })
      this.setState({names:optionsArray, value:iso3Array})
    });
  }



  handleChange = (event) => {
    let val = event.target.value;
    console.log("changed" , val);
    fetch( `https://covid19.mathdro.id/api/countries/${val}`)
    .then(response => response.json())
    .then(data => {
      console.log("handled", data);
      this.setState({selected:data})
    });
console.log("state inside change",this.state);

  }

  handleSubmit(event) {
    alert('Your favorite flavor is: ' + this.state.value);
    event.preventDefault();
    console.log("Submitted");

  }


  render() {
    console.log("inside render", this.state);
    const { names, value, selected } = this.state;
    const renderName = names.map((el,index) => {
      return(
        <Fragment key={index}>
          <option value={el}>{el}</option>
        </Fragment>
      )
    })
    // console.log("names", names);
    if(selected === null) {
      return(
        <div>
          <select value="" onChange={this.handleChange}>
            {renderName}  
          </select>
        </div>
      )
    } else {
      return (
        console.log("else statement", selected)
        <div>
        <div className="entireData-items">
        <h2>الأردن</h2>
        <div className="global-item">
          <p style={{"color": "orange", "fontWeight":"bold"}}> {selected.confirmed} </p>
          <label  style={{"color": "orange"}}>مؤكد</label>
        </div>
        <div className="global-item">
          <p style={{"color": "green", "fontWeight":"bold"}}> {selected.confirmed}</p>
          <label style={{"color": "green"}}>متعافي</label>
        </div>
        <div className="global-item">
          <p style={{"color": "red", "fontWeight":"bold"}}> {selected.confirmed}</p>
          <label style={{"color": "red"}}>وفاة</label>
        </div>

        <br></br>
        <br></br>
        <div style={{"fontSize":"10px", "color":"white"}}>{ update && (<div>Updated: {new Date(update).toLocaleString()}</div>)}</div>

      </div>

      )
    }

    return (
      <form onSubmit={this.handleSubmit}>
        <div>
          Hello
        </div>
      </form>
    );
  }
}

export default Search;
