import React, { Component } from 'react'
import styled from 'styled-components'

export default class InstructionTicker extends Component {
  render() {
    return (
      <Tcontainer className="tcontainer">
        <TickerWrap className="ticker-wrap">
          <TickerMove className="ticker-move">
            <TickerItem className="ticker-item">Lorem ipsum dolor sit amet, consectetur adipiscing elit.</TickerItem>
            <TickerItem className="ticker-item">Aliquam consequat varius consequat.</TickerItem>
            <TickerItem className="ticker-item">Fusce dapibus turpis vel nisi malesuada sollicitudin.</TickerItem>
            <TickerItem className="ticker-item">Pellentesque auctor molestie orci ut blandit.</TickerItem>
          </TickerMove>
        </TickerWrap>
      </Tcontainer>
    )
  }
}


// const TickerContainer = keyframes`
//   0% { 
//       transform: translate3d(0, 0, 0);
//     }
//   100% {
//       transform: translate3d(-100%, 0, 0);
//     }
// `;
const Tcontainer = styled.div`
  width: 100%;
  overflow: hidden;
`;

const TickerWrap = styled.div`
  width: 100%;
  padding-left: 100%;
  background-color: #eee;
`

const TickerMove = styled.div`
  display: inline-block;
  white-space: nowrap;
  padding-right: 100%;
  animation-iteration-count: infinite;
  animation-timing-function: linear;
  // animation-name: ${TickerContainer};
  animation-duration: 10s;
`;

// .ticker-move:hover{
//   animation-play-state: paused;
// }
const TickerItem = styled.div`
  display: inline-block;
  padding: 0 2rem;
`;
